# Import packagesimport osimport timeimport pandas as pdimport spotipyfrom spotipy.oauth2 import SpotifyClientCredentialsfrom dotenv import load_dotenv# Set working directory# Get env variablesos.chdir('/Users/umreenimam/documents/coding/data_analysis/music_analysis')load_dotenv()spotify_id = os.getenv('client_id')spotify_secret = os.getenv('client_secret')spotify_username = os.getenv('username')coldplay_id = os.getenv('playlist_id')# Connect to Spotify APIclient_cred_manager = SpotifyClientCredentials(spotify_id, spotify_secret)sp = spotipy.Spotify(client_credentials_manager = client_cred_manager)# Get tracks & id's def getTracks(user, playlist_id): 	ids = []	playlist = sp.user_playlist(user, playlist_id)	for item in playlist['tracks']['items']:		track = item['track']		ids.append(track['id'])	return idsids = getTracks(spotify_username, coldplay_id)# Get all track info using IDsdef getTrackFeatures(id):    meta = sp.track(id)    features = sp.audio_features(id)        # meta information    name = meta['name']    album = meta['album']['name']    artist = meta['album']['artists'][0]['name']    release_date = meta['album']['release_date']    length = meta['duration_ms']    popularity = meta['popularity']        # track features    acoustic = features[0]['acousticness']    danceability = features[0]['danceability']    energy = features[0]['energy']    instrumentalness = features[0]['instrumentalness']    liveness = features[0]['liveness']    loudness = features[0]['loudness']    speechiness = features[0]['speechiness']    tempo = features[0]['tempo']    time_signature = features[0]['time_signature']        track = [name, album, artist, release_date, length, popularity, acoustic, danceability, energy, instrumentalness, liveness, loudness, speechiness, tempo, time_signature]    return track # Loop over tracks and and create dataframetracks = []for i in range(len(ids)):    time.sleep(.5)    track = getTrackFeatures(ids[i])    tracks.append(track)    tracks_df = pd.DataFrame(tracks, columns = ['name', 'album', 'artist', 'release_date', 'length', 'popularity', 'acoustic', 'danceability', 'energy', 'instrumentalness', 'liveness', 'loudness', 'speechiness', 'tempo', 'time_signature' ])tracks_df.to_csv('coldplay_tracks.csv', sep = ',')